mapper.select:
		select * from user where 1=1
		
			and id=#{id}
		
		
			and username=#{username}
		
		
			and password=#{password}

mapper.delete:
delete from user where 1=1

	and id=#{id}


	and username=#{username}


	and password=#{password}

mapper.update:
		update user
		
			
				username=#{username},
			
			
				password=#{password}
			
		

		where id=#{id}
